export default {
	refresh_assets: () => {
		get_accounts.run(() => {
			get_classes.run(() => {
				get_asset.run(() => {
					assets.assets_class_dropdowns();
					assets.assets_all();
				}, () => showAlert('Couldnt refresh classes or assets','warning'))
			}, () => showAlert('Couldnt refresh classes or assets','warning'))
		}, () => showAlert('Couldnt refresh classes or assets','warning'))
	},
	asset_create_response: () => {
		asset_sf.run(() => {
			asset_create.run(() => 
											 {assets.refresh_assets(); showAlert('Asset Created', 'success'); get_asset.run()}, () => showAlert('Couldnt create the Asset in DB', 'error'))
		}, () => showAlert('Couldnt create the Asset in chain'))		
	},
	class_create_response: () => {
		class_sf.run(() => {
			class_create.run(() => {assets.refresh_assets(); showAlert('Class Created', 'success')}, () => showAlert('Couldnt create the Class in DB', 'error'))
		}, () => showAlert('Couldnt create the Class in chain'))		
	},
	asset_balance: () => {
		storeValue("asset_balance", undefined);
		storeValue("asset_balance", asset_balance_sf.data.amount.toString());
		return appsmith.store.asset_balance
	},
	assets_all: () => {
		storeValue("assets_table", undefined);
		storeValue("assets_table", get_data.getAssets(get_asset.data.sf_asset));
		return appsmith.store.assets_table
	},
	assets_by_account: () => {
		storeValue("assets_table", undefined);
		storeValue("assets_table", get_data.getAssets(get_asset_by_account_id.data.sf_asset));
		return appsmith.store.assets_table
	},
	assets_by_id: () => {
		storeValue("assets_table", undefined);
		storeValue("assets_table", get_data.getAssets(get_asset_by_id.data.sf_asset));
		return appsmith.store.assets_table
	},
	assets_by_class_id: () => {
		storeValue("assets_table", undefined);
		storeValue("assets_table", get_data.getAssets(get_asset_by_class_id.data.sf_asset));
		return appsmith.store.assets_table
	},
	assets_by_chain_ids: () => {
		storeValue("assets_table", undefined);
		storeValue("assets_table", get_data.getAssets(get_asset_by_chain_ids.data.sf_asset));
		return appsmith.store.assets_table
	},
	assets_class_dropdowns: () => {
		storeValue("accounts", undefined);
		storeValue("accounts", get_data.getAccounts(get_accounts.data.sf_account));
		storeValue("asset_classes", undefined);
		storeValue("asset_classes", get_data.getClasses(get_classes.data.sf_class));
		return appsmith.store.accounts;
	}
}
