actionConfiguration.body:
(data) => {
		var editedAssets = data;
		var assetArray = [];
		for (let i = 0; i < editedAssets.length; i++) {
			var newAsset = {
				"id": editedAssets[i].id,
				"name": editedAssets[i].asset_nft.name,
				"account_id": editedAssets[i].account_assets[0].account_id,
				"class_id": editedAssets[i].asset_classes[0].class_id,
				"chain_class_id": editedAssets[i].asset_classes[0].class.chain_id,
				"chain_id": editedAssets[i].asset_metadata.chain_id,
				"chain_meta": editedAssets[i].asset_metadata.chain_meta,				
			};
			assetArray.push(newAsset);
		}
		return assetArray		
	}

