() => {
		let schema_classes = [];
		let schema_assets = [];
		let schema_amounts = [];
		let length = Schema_table.tableData.length;		
		for (let i=0; i < length; i++) {
			if (!schema_classes.includes(Schema_table.tableData[i].class_id)){
				schema_classes.push(Schema_table.tableData[i].class_id)
				let assets = [];
				let amounts = [];
				for (let j=0; j < length; j++){
					if(Schema_table.tableData[j].class_id == Schema_table.tableData[i].class_id){
						assets.push(Schema_table.tableData[j].asset_id);
						amounts.push(Schema_table.tableData[j].amount);
					}
				}
				schema_assets.push(assets);
				schema_amounts.push(amounts);
			} 
		}		
		return {
        "class_ids": schema_classes,
        "asset_ids": schema_assets,
        "amounts": schema_amounts
    };
	}
