export default {
	refresh_currencies: () => {
		get_currency.run(() => {
			currencies.currencies_dropdown();
			currencies.currencies_all();
		}, () => showAlert('Couldnt refresh currencies','warning'))
	},
	currency_issue_response: () => {
		currency_sf.run(() => {
			currency_create.run(() =>
					{currencies.refresh_currencies(); showAlert('Currency Created', 'success')}, () => showAlert('Couldnt create the Currency in DB', 'error'))
		}, () => showAlert('Couldnt create the Currency in chain','error'))		
	},
	currency_mint_response: () => {
		currency_mint_sf.run(() => {
			currency_supply_mint.run(() => {
					currency_update_mint.run(()=>
											 {currencies.refresh_currencies(); showAlert('Currency Minted', 'success')},() => showAlert('Couldnt Store the Currency supply in DB', 'error'))
		}, () => showAlert('Couldnt get the Supply of Currency in Chain','error'))
		}, () => showAlert('Couldnt Mint the Currency in chain','error'))		
	},
	currency_burn_response: () => {
		currency_burn_sf.run(() => {
			currency_supply_burn.run(() => {
					currency_update_burn.run(()=>
											 {currencies.refresh_currencies(); showAlert('Currency Burned', 'success')},() => showAlert('Couldnt Store the Currency supply in DB', 'error'))
		}, () => showAlert('Couldnt get the Supply of Currency in Chain','error'))
		}, () => showAlert('Couldnt Burn the Currency in chain','error'))		
	},
	currency_supply_burn:() => {
		currency_supply_burn.run();
	},
	currency_total_supply: () => {
		storeValue("currency_total_supply", undefined);
		storeValue("currency_total_supply", currency_issuance_sf.data.amount.toString());
		return appsmith.store.currency_total_supply
	},
	currency_current_supply: () => {
		storeValue("currency_current_supply", undefined);
		storeValue("currency_current_supply", currency_supply_sf.data.total_supply.toString());
		return appsmith.store.currency_current_supply
	},
	currencies_all: () => {
		storeValue("currency_table", undefined);
		storeValue("currency_table", get_data.getCurrencyTable(get_currency.data.sf_currency));
		return appsmith.store.currency_table
	},
	currencies_dropdown: () => {
		storeValue("currency_dropdown", undefined);
		storeValue("currency_dropdown", get_data.getCurrency(get_currency.data.sf_currency));
		return appsmith.store.currency_dropdown
	}
}
